import React from 'react'
import Slider from 'react-slick';
import ava01 from '../../assets/images/ava-1.jpg';
import ava02 from '../../assets/images/ava-2.jpg';
import ava03 from '../../assets/images/ava-3.jpg';

export const Testimonials = () => {
  return <Slider>
       <div className="testimonial py-4 px-3">
           <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla facilisi. 
            Sed vel ipsum non dolor ullamcorper tempus. Donec ultricies, felis in 
            fermentum fermentum, nulla nisi ultricies dui, at facilisis nunc justo a neque.</p>
           
           <div className="d-flex align-items-center gap-4 ">
             <img src={ava01} alt="" className="testimonial_avatar" />
             <h5 className="mb-0">John Doe</h5>
             <span>Founder, ABC Company</span>
            </div>
    
       </div>
  </Slider>
}
