import React, { useState, useEffect } from 'react';
import { FaPlane, FaCalendarAlt } from 'react-icons/fa';

const MyTrip = () => {
    const [tripOrders, setTripOrders] = useState([]);
    const [formData, setFormData] = useState({
        userId: '',
        destination: '',
        startDate: '',
        endDate: '',
        numberOfTravelers: 1,
        specialRequests: '',
    });

    useEffect(() => {
        fetchTripOrders();
    }, []);

    const fetchTripOrders = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/trip-orders');
            const data = await response.json();
            setTripOrders(data);
        } catch (error) {
            console.error('Error fetching trip orders:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: name === 'numberOfTravelers' ? parseInt(value) : value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await fetch('http://localhost:5000/api/trip-orders', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });
            fetchTripOrders();
            setFormData({
                userId: '',
                destination: '',
                startDate: '',
                endDate: '',
                numberOfTravelers: 1,
                specialRequests: '',
            });
        } catch (error) {
            console.error('Error submitting trip order:', error);
        }
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Trip Order Page</h1>
            <form onSubmit={handleSubmit} className="mb-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                        <label className="block mb-2">User ID</label>
                        <input type="text" name="userId" value={formData.userId} onChange={handleChange} className="border rounded p-2 w-full" required />
                    </div>
                    <div>
                        <label className="block mb-2">Destination</label>
                        <input type="text" name="destination" value={formData.destination} onChange={handleChange} className="border rounded p-2 w-full" required />
                    </div>
                    <div>
                        <label className="block mb-2">Start Date</label>
                        <input type="date" name="startDate" value={formData.startDate} onChange={handleChange} className="border rounded p-2 w-full" required />
                    </div>
                    <div>
                        <label className="block mb-2">End Date</label>
                        <input type="date" name="endDate" value={formData.endDate} onChange={handleChange} className="border rounded p-2 w-full" required />
                    </div>
                    <div>
                        <label className="block mb-2">Number of Travelers</label>
                        <input type="number" name="numberOfTravelers" value={formData.numberOfTravelers} onChange={handleChange} className="border rounded p-2 w-full" min="1" required />
                    </div>
                    <div>
                        <label className="block mb-2">Special Requests</label>
                        <textarea name="specialRequests" value={formData.specialRequests} onChange={handleChange} className="border rounded p-2 w-full" />
                    </div>
                </div>
                <button type="submit" className="mt-4 bg-blue-500 text-white rounded p-2">Submit Order</button>
            </form>
            <h2 className="text-xl font-semibold mb-2">Existing Trip Orders</h2>
            <ul className="list-disc pl-5">
                {tripOrders.map(order => (
                    <li key={order._id} className="mb-2">
                        <div className="flex items-center">
                            <FaPlane className="mr-2" />
                            <span>{order.tripDetails?.destination || 'N/A'} - {order.status || 'Pending'}</span>
                        </div>
                        <div className="text-sm text-gray-600">
                            <FaCalendarAlt className="inline mr-1" />
                            {order.tripDetails ? (
                                <>
                                    {new Date(order.tripDetails.startDate).toLocaleDateString()} to {new Date(order.tripDetails.endDate).toLocaleDateString()} | Travelers: {order.tripDetails.numberOfTravelers}
                                </>
                            ) : 'Dates not available'}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MyTrip;
