// src/App.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "remixicon/fonts/remixicon.css";
import "./App.css"; // Assuming you will create a CSS file for styling

const App = () => {
  const [bookings, setBookings] = useState([]);
  const [newBooking, setNewBooking] = useState({
    userId: "",
    userEmail: "",
    tourName: "",
    fullName: "",
    guestSize: 1,
    phone: "",
    bookAt: "",
  });

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      const response = await axios.get("/api/bookings"); // Adjust the endpoint as needed
      setBookings(response.data.data);
    } catch (error) {
      console.error("Error fetching bookings:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewBooking({ ...newBooking, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/api/bookings", newBooking); // Adjust the endpoint as needed
      alert(response.data.message);
      fetchBookings(); // Refresh the bookings list
    } catch (error) {
      console.error("Error creating booking:", error);
    }
  };

  return (
    <div className="App">
      <h1>Booking System</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="userId"
          placeholder="User  ID"
          onChange={handleChange}
        />
        <input
          type="email"
          name="userEmail"
          placeholder="User  Email"
          onChange={handleChange}
        />
        <input
          type="text"
          name="tourName"
          placeholder="Tour Name"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="fullName"
          placeholder="Full Name"
          required
          onChange={handleChange}
        />
        <input
          type="number"
          name="guestSize"
          placeholder="Guest Size"
          required
          onChange={handleChange}
        />
        <input
          type="tel"
          name="phone"
          placeholder="Phone"
          required
          onChange={handleChange}
        />
        <input
          type="datetime-local"
          name="bookAt"
          required
          onChange={handleChange}
        />
        <button type="submit">
          <i className="ri-add-line"></i> Book Now
        </button>
      </form>

      <h2>All Bookings</h2>
      <ul>
        {bookings.map((booking) => (
          <li key={booking._id}>
            <strong>{booking.fullName}</strong> - {booking.tourName} 
            <button onClick={() => alert(JSON.stringify(booking))}>
              <i className="ri-eye-line"></i> View
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MyTrip
