import React, {useState, useContext} from 'react'
import '../Booking/booking.css';
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'reactstrap';

import {useNavigate} from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext.js';
import { BASE_URL } from '../../Utils/config.js';

const Booking = ({tour, avgRating}) => {

   const { price , reviews, title} = tour;

   const navigate = useNavigate();

   const {user } = useContext(AuthContext);

   const [booking, setBooking] = useState({
      userId: user && user._id,
      userEmail: user && user.email,
      tourName: title,
      fullName:'',
      phone:'',
      guestSize:'',
      bookAt:'',
   });

   const handleChange = e => {
      setBooking(prev=>({...prev,[e.target.id]:e.target.value}))
   } ;

   const serviceFee = 10;
   const totalAmount = Number(price) * Number(booking.guestSize) + Number(serviceFee)

   const handleClick = async e => {
      e.preventDefault();

      console.log(booking);

      try {

         if(!user || user===undefined ||  user===null){
            return alert('Please Sign In')
         }

         const res = await fetch(`${BASE_URL}/booking`, {
            method:'post',
            headers:{'Content-Type':'application/json'},
            credentials:'include',
            body:JSON.stringify(booking),
         })

         const result = await res.json()

         if(!res.ok){
            return alert(result.message)
         }

         navigate('/thank-you');
         
      } catch (err) {

         alert(err.message);
         
      }
   };

   const B = async () => {
      // validation 
      if (name === "" || address === "" || pincode === "" || phoneNumber === "") {
          return toast.error("All fields are required", {
              position: "top-center",
              autoClose: 1000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
              theme: "colored",
          })
      }


      const addressInfo = {
          name,
          address,
          pincode,
          phoneNumber,
          date: new Date().toLocaleString(
              "en-US",
              {
                  month: "short",
                  day: "2-digit",
                  year: "numeric",
              }
          )
      }

      var options = {
          key: "rzp_test_2QFT34Wt5XmXm0",
          key_secret: "nKBKjfa3YAwiAVkDYkDnrDZz",
          amount: parseInt(grandTotal * 100),
          currency: "INR",
          order_receipt: 'order_rcptid_' + name,
          name: "Kitchen",
          description: "for testing purpose",
          handler: function (response) {
              console.log(response);
              toast.success('Payment Successful');

              const paymentId = response.razorpay_payment_id;
              const orderInfo = {
                  cartItems,
                  addressInfo,
                  date: new Date().toLocaleString(
                      "en-US",
                      {
                          month: "short",
                          day: "2-digit",
                          year: "numeric",
                      }
                  ),
                  // email: JSON.parse(localStorage.getItem("user")).user.email,
                  // userid: JSON.parse(localStorage.getItem("user")).user.uid,
                  paymentId
              }

              try {
                  const result = addDoc(collection(fireDB, "orders"), orderInfo)
              } catch (error) {
                  console.log(error)
              }

          },


          theme: {
              color: "#3399cc"
          }
      };

      var pay = new window.Razorpay(options);
      pay.open();
      console.log(pay)
  }

  return (
    <>
      <div className="booking">
         <div className="booking__top d-flex align-items-center justify-content-between">
             <h3>${price} <span>/per person</span></h3>
             <span className="tour__rating d-flex align-items-center">
                <i class="ri-star-fill"></i> {avgRating === 0 ? null : avgRating} ({reviews?.length})
            </span> 
         </div>

        {/*==================================Booking Form Start==================================*/}

          <div className="booking__form">
              <h5>Information</h5>
              <Form className="booking__info-form" onSubmit={handleClick}>
                 <FormGroup>
                    <input type="text" placeholder="Full Name" id="fullName" required onChange={handleChange} />
                 </FormGroup>
                 <FormGroup>
                    <input type="number" placeholder="Phone" id="phone" required onChange={handleChange} />
                 </FormGroup>
                 <FormGroup className="d-flex align-items-center gap-3">
                    <input type="date" placeholder="" id="bookAt" required onChange={handleChange} />
                    <input type="number" placeholder="Guest" id="guestSize" required onChange={handleChange} />
                 </FormGroup>
              </Form>
          </div>
                              
        {/*==================================Booking Form end==================================*/}

        {/*==================================Booking bottom==================================*/}

        <div className="booking__bottom">
         <ListGroup>
              <ListGroupItem className="border-0 px-0">
                    <h5 className="d-flex align-items-center gap-1">${price} <i class="ri-close-line"></i> 1 person</h5>
                    <span> ${price}</span>
              </ListGroupItem>
              <ListGroupItem className="border-0 px-0">
                    <h5>Service charge</h5>
                    <span> ${serviceFee}</span>
              </ListGroupItem>
              <ListGroupItem className="border-0 px-0 total">
                    <h5>Total</h5>
                    <span> ${totalAmount}</span>
              </ListGroupItem>
         </ListGroup>
         <Button type="submit" className="btn primary__btn w-100 mt-4" onClick={handleClick}>Book Now</Button>
        </div>

      </div>
    </>
  )
}

export default Booking
